SELECT

FROM customer
WHERE isDel = 0

	AND CustomerId = #{customerId,jdbcType=VARCHAR}


	AND CustomerCode LIKE CONCAT('%','${customerCode}','%')


	AND LastName LIKE CONCAT('%','${lastName}','%')


