SELECT o.OrderId, o.OrderNo, o.OrderNoIn, o.TotalPeople, o.CommonTourFee, o.Tax, o.State, o.UserId, o.TourTypeId, o.Contact, o.TourCode, o.TourId, o.CreateTime, o.PeerId, o.OrderType, o.OrderTourType, o.FlightPnr, o.NonGroupType, o.PeerUserId, o.PeerUserFee, o.NoticeState, o.Brand, o.IsSelfOrganize, o.Rate, o.PayState, o.WarnState, o.PeerState, o.ReviewState, o.CostState, o.ModifyDate,o.OrdersTotalId,o.DeptId,o.CheckTime,o.UserName, t.ScheduleOfArriveTime as ArriveDate, t.LineName FROM orders o, tour_info_for_order t
o.orderId=t.orderId AND o.State!=5 AND o.State!=6
AND o.DeptId = #{record.deptId,jdbcType=VARCHAR}
AND o.TourTypeId = #{record.tourTypeId,jdbcType=VARCHAR}
AND t.GroupLineId = #{record.groupLineId,jdbcType=VARCHAR}
AND DATE_FORMAT(t.ScheduleOfArriveTime,"%Y-%m") >= #{record.beginningDate,jdbcType=TIMESTAMP}
AND t.ScheduleOfArriveTime <= #{record.endingDate,jdbcType=TIMESTAMP}

