SELECT expm.*, ss.SprCheck FROM ( SELECT o.OrderId, o.OrderNo, o.TotalPeople, o.Tax, o.State, a.id, a.UserName, o.TourCode, o.userId, o.CreateTime, o.orderTourType, o.WarnState, o.ReviewState, t.TourId, t.arriveDateTime FROM Orders o, zs_admin a, Dept d, Tour t WHERE o.UserId=a.id AND a.DeptId=d.DeptId AND o.TourId=t.TourId
AND o.OrderId=#{record.id}
AND o.OrderNo LIKE CONCAT(CONCAT('%',#{record.orderNo}),'%')
AND o.orderTourType=#{record.orderTourType}
AND o.Tax=#{record.tax}
AND (o.Tax=2 or o.Tax=4)
AND o.State=#{record.state}
AND o.WarnState=#{record.warnState}
AND a.userName LIKE CONCAT(CONCAT('%',#{record.userName}),'%')
AND d.DeptId=#{record.deptId}
AND t.TourCode LIKE CONCAT(CONCAT('%',#{record.tourCode}),'%')
ORDER BY o.WarnState DESC,t.arriveDateTime DESC ) expm LEFT JOIN( SELECT sr.TourId, sr.SprCheck, sc.UserIdOfAgent FROM SupplierPriceRemark sr, SupplierCheck sc WHERE sr.SupplierCheckId=sc.SupplierCheckId AND sr.Type=1 AND (sr.isDel=0 OR sr.isDel IS NULL) AND (sr.SprCheck=1 OR sr.SprCheck=3 OR sr.SprCheck=5)
AND sr.SprCheck=#{record.sprCheck}
GROUP BY sr.TourId,sc.UserIdOfAgent ORDER BY sr.SprCheck ASC ) ss ON expm.TourId=ss.TourId AND expm.id=ss.UserIdOfAgent AND (ss.SprCheck=1 OR ss.SprCheck=3 OR ss.SprCheck=5) WHERE 1=1
AND ss.SprCheck=#{record.sprCheck}
GROUP BY expm.OrderId ORDER BY expm.WarnState DESC,ss.SprCheck ASC ,expm.arriveDateTime DESC

