mapper.sql:
SELECT expm.orderId,expm.OrdersTotalId,expm.OrderNo,expm.userName,expm.CommonTourFee,expm.state, expm.tax,expm.createTime,expm.userId,expm.CheckTime,expm.totalPeople,expm.tourCode,v.Name as peerId, ss.SprCheck,expm.tourTypeId FROM ( SELECT o.orderId,o.OrdersTotalId,o.OrderNo,o.userName,ROUND(o.CommonTourFee,2) as CommonTourFee,o.state, o.tax,o.createTime,o.userId,o.CheckTime,o.totalPeople,o.tourCode,o.peerId,o.tourTypeId FROM orders o ,tour_info_for_order t WHERE t.OrderId=o.OrderId
AND o.OrdersTotalId = #{record.ordersTotalId,jdbcType=VARCHAR}
AND o.OrderNo LIKE CONCAT('%','${record.orderNo}','%')
AND o.TotalPeople = #{record.totalPeople,jdbcType=INTEGER}
AND o.DeptId = #{record.deptId,jdbcType=VARCHAR}
AND o.UserId = #{record.userId,jdbcType=VARCHAR}
AND o.IsSelfOrganize = #{record.isSelfOrganize,jdbcType=TINYINT}
AND o.CreateTime >= #{record.bookingBeginningDate,jdbcType=TIMESTAMP}
AND DATE_FORMAT(o.CreateTime,'%Y-%m-%d') <= #{record.bookingEndingDate,jdbcType=TIMESTAMP}
AND t.ScheduleOfArriveTime >= #{record.arrivalBeginningDate,jdbcType=TIMESTAMP}
AND t.ScheduleOfArriveTime <= #{record.arrivalEndingDate,jdbcType=TIMESTAMP}
AND o.CheckTime >= #{record.settlementDateBeg,jdbcType=TIMESTAMP}
AND o.CheckTime <= #{record.settlementDateEnd,jdbcType=TIMESTAMP}
AND o.userName LIKE CONCAT('%','${record.userName}','%')
AND o.Tax=#{record.tax}
AND (o.Tax=2 or o.Tax=4)
AND o.TourCode LIKE CONCAT('%','${record.tourCode}','%')
)expm LEFT JOIN vender v ON v.venderId=expm.peerId LEFT JOIN( SELECT sr.SprCheck, sr.orderId FROM SupplierPriceRemark sr WHERE sr.Type=1 AND (sr.isDel=0 OR sr.isDel IS NULL) AND (sr.SprCheck=1 OR sr.SprCheck=3 OR sr.SprCheck=5) )ss ON ss.orderId=expm.orderId
ss.SprCheck=#{record.sprCheck}
AND v.Name LIKE CONCAT('%','${record.peerId}','%')
GROUP BY expm.orderId ORDER BY expm.CreateTime DESC,expm.tourCode DESC

